import React from 'react';
import './Home.css';
import { useState, useEffect } from 'react';

function Home() {
  const [tarifler, setTarifler] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/tarifler")
    .then(res => res.json())
    .then(data => setTarifler(data))
  }, []);

  return (
    <div className="row mt-3">
      {
        tarifler && tarifler.map(tarif => (
          <div className="col-3 mb-3">
            <div className="card">
              <img src={`img/${tarif.resim}`} alt={tarif.baslik} />
              <div className="card-body">
                <h5 className="card-title">{ tarif.baslik }</h5>
                <p className="aciklama">{ tarif.aciklama }</p>
                <a href={ tarif.url } className="btn btn-outline-primary">Tarifi Ä°ncele</a>
              </div>
            </div>
          </div>  
        ))
      }
    </div>
  )
}

export default Home;
