import { useState, useEffect } from "react";

const useFetch = (url) => {
    const [veri, setVeri] = useState(null);

    useEffect(() => {
        fetch(url)
        .then(res => res.json())
        .then(data => setVeri(data))
    }, [url])

    return (
        veri    
    )
}

export default useFetch;
