import { useState, useEffect } from "react";

const useFetch = (url) => {
    const [data, setData] = useState(url);

    useEffect(() => {
        fetch(url)
        .then(res => res.json())
        .then(data => setData(data))
    }, [url])

    return (
        data
    )
}

export default useFetch;
